mapper.select:
select * from orders
where member_num = #{memberNum}
order by order_num desc

mapper.update:
	update orders set
	status = '고객 주문취소 요청', reason = #{reason} 
	where order_num =#{orderNum}

update orders set
status = #{status}, reason = #{reason} 
where order_num =#{orderNum}

update orders set
status = '배송중', track_num = #{trackNum}
where order_num =#{orderNum}
